import axios from 'axios';

export function getBooks(
    limit=10,
    start=0,
    order='asc'
){
    const request = axios.get(`http://localhost:5000/api/books?limit=${limit}&skip=${start}&order=${order}`)
                        .then(response => response.data )
    console.log(request)
    return{
        type:'GET_BOOKS',
        payload:request
    }
}
